// ajax 封装插件, 使用 axios
import Vue from 'vue'
import axios from 'axios'
import Config from '@/config'
import ErrorCode from '@/config/error-code'
import store from '@/store'
import {
  getToken,
  saveAccessToken
} from '@/lin/util/token'

const config = {
  baseURL: Config.baseURL || process.env.apiUrl || '',
  timeout: 10 * 1000, // 请求超时时间设置
  crossDomain: true,
  // withCredentials: true, // Check cross-site Access-Control
  // 定义可获得的http响应状态码
  // return true、设置为null或者undefined，promise将resolved,否则将rejected
  validateStatus(status) {
    return status >= 200 && status < 510
  },
}

/**
 * 错误码是否是refresh相关
 * @param {number} code 错误码
 */
function refreshTokenException(code) {
  let flag = false
  const codes = [10000, 10042, 10050, 10052]
  if (codes.includes(code)) {
    flag = true
  }
  return flag
}

// const retryTime = 2 // 请求失败重试次数
// const retryDelay = 1500 // 请求失败重试间隔

// 创建请求实例
const _axios = axios.create(config)

_axios.interceptors.request.use(
  originConfig => {
    // 有 API 请求重新计时
    Vue.prototype.$_lin_jump()

    const reqConfig = {
      ...originConfig
    }

    // step1: 容错处理
    if (!reqConfig.url) {
      /* eslint-disable-next-line */
      console.error('request need url')
      throw new Error({
        source: 'axiosInterceptors',
        message: 'request need url',
      })
    }

    if (!reqConfig.method) {
      // 默认使用 get 请求
      reqConfig.method = 'get'
    }
    // 大小写容错
    reqConfig.method = reqConfig.method.toLowerCase()

    // 参数容错
    if (reqConfig.method === 'get') {
      if (!reqConfig.params) {
        // 防止字段用错
        reqConfig.params = reqConfig.data || {}
      }
    } else if (reqConfig.method === 'post') {
      if (!reqConfig.data) {
        // 防止字段用错
        reqConfig.data = reqConfig.params || {}
      }

      // 检测是否包含文件类型, 若包含则进行 formData 封装
      let hasFile = false
      Object.keys(reqConfig.data).forEach(key => {
        if (typeof reqConfig.data[key] === 'object') {
          const item = reqConfig.data[key]
          if (item instanceof FileList || item instanceof File || item instanceof Blob) {
            hasFile = true
          }
        }
      })

      // 检测到存在文件使用 FormData 提交数据
      if (hasFile) {
        const formData = new FormData()
        Object.keys(reqConfig.data).forEach(key => {
          formData.append(key, reqConfig.data[key])
        })
        reqConfig.data = formData
      }
    } else {
      // TODO: 其他类型请求数据格式处理
      /* eslint-disable-next-line */
      console.warn(`其他请求类型: ${reqConfig.method}, 暂无自动处理`)
    }
    // step2: permission 处理
    if (reqConfig.url === 'recruit/user/refresh') {
      const refreshToken = getToken('refresh_token')
      if (refreshToken) {
        // eslint-disable-next-line no-param-reassign
        reqConfig.headers.Authorization = refreshToken
      }
    } else {
      // 有access_token
      const accessToken = getToken('access_token')
      if (accessToken) {
        // eslint-disable-next-line no-param-reassign
        reqConfig.headers.Authorization = accessToken
      }
    }
    return reqConfig
  },
  error => {
    Promise.reject(error)
  },
)

// Add a response interceptor
_axios.interceptors.response.use(
  async res => {
      let {
        code,
        message
      } = res.data // eslint-disable-line
      if (res.status.toString().charAt(0) === '2') {
        return res.data
      }
      return new Promise(async (resolve, reject) => {
        const {
          url
        } = res.config

        // refreshToken相关，直接登出
        if (refreshTokenException(code)) {
          setTimeout(() => {
            store.dispatch('loginOut')
            const {
              origin
            } = window.location
            window.location.href = origin
          }, 1500)
          return resolve(null)
        }
        // assessToken相关，刷新令牌
        if (code === 10041 || code === 10051) {
          const cache = {}
          if (cache.url !== url) {
            cache.url = url
            const refreshResult = await _axios('recruit/user/refresh')
            saveAccessToken(refreshResult.access_token)
            // 将上次失败请求重发
            const result = await _axios(res.config)
            return resolve(result)
          }
        }
        // 第一种情况：默认直接提示后端返回的异常信息；特殊情况：如果本次请求添加了 handleError: true，用户自己try catch，框架不做处理
        if (res.config.handleError) {
          return reject(res)
        }
        // 第二种情况：采用前端自己的一套异常提示信息；特殊情况：如果本次请求添加了 showBackend: true, 弹出后端返回错误信息。
        if (Config.useFrontEndErrorMsg && !res.config.showBackend) {
          // 弹出前端自定义错误信息
          const errorArr = Object.entries(ErrorCode).filter(v => v[0] === code.toString())
          // 匹配到前端自定义的错误码
          if (errorArr.length > 0 && errorArr[0][1] !== '') {
            message = errorArr[0][1] // eslint-disable-line
          } else {
            message = ErrorCode['777']
          }
        }

        Vue.prototype.$message({
          message,
          type: 'error',
        })
        reject()
      })
    },
    error => {
      if (!error.response) {
        Vue.prototype.$notify({
          title: 'Network Error',
          dangerouslyUseHTMLString: true,
          message: '<strong class="my-notify">请求错误,服务器无响应.</strong>',
        })
        console.log('error', error)
      }

      // 判断请求超时
      if (error.code === 'ECONNABORTED' && error.message.indexOf('timeout') !== -1) {
        Vue.prototype.$message({
          type: 'warning',
          message: '请求超时',
        })
      }
      return Promise.reject(error)
    },
)

// eslint-disable-next-line
Plugin.install = function (Vue, options) {
  // eslint-disable-next-line
  Vue.axios = _axios
  window.axios = _axios
  Object.defineProperties(Vue.prototype, {
    axios: {
      get() {
        return _axios
      },
    },
    $axios: {
      get() {
        return _axios
      },
    },
  })
}

if (!Vue.axios) {
  Vue.use(Plugin)
}

