/**
 * 通用接口定义
 */
export interface DataPointType {
    [k: string]: any;
}
export interface PointObject {
    x: number;
    y: number;
}
export interface ShapeDrawCFG {
    color: string | null | undefined;
    isInCircle: boolean | undefined;
    x: number | number[];
    y: number | number[];
    shape: string | undefined | null;
    size: number | undefined | null;
    opacity: number | undefined;
    origin: DataPointType;
    geomType?: string;
    id?: string;
    points?: DataPointType[];
    nextPoints?: PointObject[];
    splitedIndex?: number;
    text?: string | null;
    style?: DataPointType | null;
    yIndex?: number;
    constraint?: [number, number][];
    showSinglePoint?: boolean;
    connectNulls?: boolean;
}
export interface ShapePointInfo {
    x: number | number[];
    y: number | number[];
    y0?: number;
    size?: number;
    _size?: number;
}
export interface ShapeStateCfg {
    origin: DataPointType;
    [key: string]: any;
}
export interface ShapeMarkerCfg {
    isInCircle?: boolean;
    color?: string[];
}
export interface AdjustCfg {
    type: string;
    marginRatio?: number;
    reverseOrder?: boolean;
    dodgeBy?: string;
}
